  // ------------ WISHLIST ------------
  // store as an array of artwork IDs
  wishlist: () => LS.get("wishlist", []),
  saveWishlist: (w) => {
    LS.set("wishlist", w);
    emit("wishlist:changed");
  },
  addToWishlist: (id) => {
    const set = new Set(LS.get("wishlist", []));
    set.add(id);
    LS.set("wishlist", [...set]);
    emit("wishlist:changed");
  },
  removeFromWishlist: (id) => {
    const next = LS.get("wishlist", []).filter((x) => x !== id);
    LS.set("wishlist", next);
    emit("wishlist:changed");
  },

  // ------------ CART ------------
  // store as an array of artwork IDs
  cart: () => LS.get("cart", []),
  saveCart: (c) => {
    LS.set("cart", c);
    emit("cart:changed");
  },
  addToCart: (id) => {
    const next = [...LS.get("cart", []), id];
    LS.set("cart", next);
    emit("cart:changed");
  },
  removeFromCart: (id) => {
    const next = LS.get("cart", []).filter((x) => x !== id);
    LS.set("cart", next);
    emit("cart:changed");
  },
